me:AddMsg("[NAME]Đi đến di tích Long Đài, sau một phen tìm kiếm, vậy mà lại thật sự tìm được một vài bảo vật .");
local tbTable = GameMain:GetMod("MapStoryHelper");
tbTable:VisitAward(me);
else
me:AddMsg("[NAME]Đi đến di tích Long Đài, thử tìm một chút cơ duyên .Nhưng mà nhất thời vô ý, rơi vào cái bẫy của Long tộc thượng cổ .");me:AddMsg("[NAME]Dốc hết toàn thân thủ đoạn, mới thoát được tính mệnh .");me:AddDamageRandomPart(1,"Fracture4", 1,"Cấm chế Long Huyệt");
end
elseif world:GetItemCount("Item_BossFeng_FengYin")< 1 and world:GetWorldFlag(82) == 0 then
if me:GetLuck() >world:RandomInt(1,15) then
me:AddMsg("[NAME]Đi đến di tích Long Đài, sau một phen tìm kiếm, vậy mà lại thật sự tìm được một vài bảo vật .");
local tbTable = GameMain:GetMod("MapStoryHelper");
