me:DropAwardItem("Item_BossFeng_FengYin", 1);
else
me:AddMsg("[NAME]Đi đến di tích Long Đài, thử tìm một chút cơ duyên .Nhưng mà nhất thời vô ý, rơi vào cái bẫy của Long tộc thượng cổ .");me:AddMsg("[NAME]Dốc hết toàn thân thủ đoạn, mới thoát được tính mệnh .");me:AddDamageRandomPart(1,"Fracture4", 1,"Cấm chế Long Huyệt");
end
end
elseif story.Place =="Place_GuZhanChang" then
local nY = world:RandomInt(5, 12);
