Text.Selections.1.OKResult:

local nR = me:RandomInt(1,15);
if 1 <= nR and nR <= 6 then
	local sN  = me:ParseNpcStory("[rand_lable=desc_time,desc_place,desc_weather]，[name]在[obj_action]时，突然心中一动，对天道又多了许多感悟。");
	me:AddMsg(sN);me:AddMemery(sN);me:AddTreeExp(me:GetDaoHang()*400);
elseif 6 <  nR and nR <= 7 then	
	local sN  = me:ParseNpcStory("[rand_lable=desc_time,desc_place,desc_weather]，[name]在[obj_action]时，突然于修行之上有所领悟。[name]赶忙将这一丝灵光记录下来。");
	me:AddMsg(sN);me:AddMemery(sN);me:DropRandomItem("Esoterica")
elseif 7 <  nR and nR <= 10 then	
	if me:IsTouchNeck() == false then
		local sN  = me:ParseNpcStory("[rand_lable=desc_time,desc_place,desc_weather]，[name]在[obj_action]时，突然领会到[obj_gong]的一丝真意。[name]当场沉浸在这一化境中，等回过神来，发现修为已然大进。");
		me:AddMsg(sN);me:AddMemery(sN);
		if me:GetGLevel() > 6 then
			me:AddPracticeResource("Stage",me:GetGLevel() * 2000)
		else
			me:AddPracticeResource("Stage",me:GetGLevel() * 100)
		end
	else
		local sN  = me:ParseNpcStory("[rand_lable=desc_time,desc_place,desc_weather]，[name]在[obj_action]时，突然领会到[obj_gong]的一丝真意。可惜的是[name]的修行遇到了瓶颈，并没能抓住这一丝灵感。");
		me:AddMsg(sN);me:AddMemery(sN);
	end
elseif 10 <  nR and nR <= 14 then	
	if world.DayCount < 100 then
		if me:IsTouchNeck() == false then
			local sN  = me:ParseNpcStory("[rand_lable=desc_time,desc_place,desc_weather]，[name]在[obj_action]时，突然领会到[obj_gong]的一丝真意。[name]当场沉浸在这一化境中，等回过神来，发现修为已然大进。");
			me:AddMsg(sN);me:AddMemery(sN);
			if me:GetGLevel() > 6 then
				me:AddPracticeResource("Stage",me:GetGLevel() * 2000)
			else
				me:AddPracticeResource("Stage",me:GetGLevel() * 100)
			end
		else
			local sN  = me:ParseNpcStory("[rand_lable=desc_time,desc_place,desc_weather]，[name]在[obj_action]时，突然领会到[obj_gong]的一丝真意。可惜的是[name]的修行遇到了瓶颈，并没能抓住这一丝灵感。");
			me:AddMsg(sN);me:AddMemery(sN);
		end
	else
		local sN  = me:ParseNpcStory("[rand_lable=desc_time,desc_place,desc_weather]，[name]在[obj_action]时，突然感觉内息一阵紊乱，全身灵气竟然失守。[name]连忙运功镇压，但也已经受了不轻的内伤。");
		me:AddMsg(sN);me:AddMemery(sN);me:AddDamageRandomPart(5,"LingSpillsInjury",me:RandomFloat(0,1),"走火入魔");me:AddDamageRandomPart(5,"LingSpillsInjury",me:RandomFloat(0,1),"走火入魔");me:AddDamageRandomPart(5,"LingSpillsInjury",me:RandomFloat(0,1),"走火入魔");
	end
end

