elseif nT == 2 then
world:BeginWeather("LingUpWave_Little");
MessageMgr:AddChainEventMessage(1,-1,"Huyệt động thần bí rất thỏa mãn với lần cúng tế này, Nó đã thực hiện"..meName.."Nguyện vọng .", 0, 0, null,"Huyệt động thần bí");
elseif nT == 3 then
